import { createSlice } from '@reduxjs/toolkit';

const userSlice = createSlice({
    name: 'user',
    initialState: {
        users: {
            allUser: null,
            isFetching: false,
            error: false,
        },
        allUsers: {
            allUsers: [],
            isFetching: false,
            error: false,
        },
    },
    reducers: {
        getUserStart: (state) => {
            state.users.isFetching = true;
        },
        getUserSuccess: (state, action) => {
            state.users.isFetching = false;
            state.users.allUser = action.payload;
        },
        getUserFailed: (state) => {
            state.users.isFetching = false;
            state.users.error = true;
        },
        deleteUserStart: (state) => {
            state.allUsers.isFetching = true;
        },
        deleteUserSuccess: (state) => {
            state.allUsers.isFetching = false;
            state.allUsers.allUser = [];
        },
        deleteUserFailed: (state) => {
            state.allUsers.isFetching = false;
            state.allUsers.error = true;
        },
    },
});

export const { getUserStart, getUserSuccess, getUserFailed, deleteUserStart, deleteUserSuccess, deleteUserFailed } =
    userSlice.actions;
export default userSlice.reducer;
