import React, { useRef } from 'react';
import styles from './Header.module.scss';
import classNames from 'classnames/bind';
import { HomeIcon, SearchIcon, SpotifyIcon } from '../../../assets/Icon';

const cx = classNames.bind(styles);

export default function Header() {
    const inputRef = useRef(null);
    return (
        <div className={cx('header-wrapper')}>
            <span className={cx('logo')}>
                <SpotifyIcon />
            </span>

            <div className={cx('search-container')}>
                <button className={cx('home-btn')} style={{ position: 'relative' }}>
                    <HomeIcon />
                </button>

                <div className={cx('search-input-container')}>
                    <span className={cx('search-icon')} style={{ height: '100%' }}>
                        {/* click -> toggle color */}
                        <SearchIcon />
                    </span>
                    <input className={cx('search-input')} spellCheck="none" placeholder="What do you want to play?" />
                </div>
            </div>
        </div>
    );
}
