import { combineReducers, configureStore } from '@reduxjs/toolkit';
import authReducer from './authSlice';
import songReducer from './songSlice';
import artistReducer from './artistSlice';
import userReducer from './userSlice';
import { persistStore, persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage'; // dùng localStorage
import rootReducer from './rootReducer';

const persistConfig = {
    key: 'root', // key lưu trong localStorage
    storage, // loại storage
    whitelist: ['auth'], // chỉ persist state 'auth'
};

rootReducer = combineReducers({
    auth: authReducer,
    users: userReducer,
    songs: songReducer,
    artists: artistReducer,
});
const persistedReducer = persistReducer(persistConfig, rootReducer);

export const store = configureStore({
    reducer: persistedReducer,
    middleware: (getDefaultMiddleware) => getDefaultMiddleware({ serializableCheck: false }), // để tránh warning non-serializable
});

export default configureStore({
    reducer: {
        auth: authReducer,
        users: userReducer,
        songs: songReducer,
        artists: artistReducer,
    },
});

export const persistor = persistStore(store);
