import { createSlice } from '@reduxjs/toolkit';

const userSlice = createSlice({
    name: 'user',
    initialState: {
        users: {
            allUser: null,
            isFetching: false,
            error: false,
        },
    },
    reducers: {
        allUserStart: (state) => {
            state.users.isFetching = true;
        },
        allUserSuccess: (state, action) => {
            state.users.isFetching = false;
            state.action.allUser = action.payload;
        },
        allUserFailed: (state) => {
            state.users.isFetching = false;
            state.users.error = true;
        },
    },
});

export const { allUserStart, allUserSuccess, allUserFailed } = userSlice.actions;
export default userSlice.reducer;
