import classNames from 'classnames/bind';
import style from './Login.module.scss';
import { SpotifyIcon } from '../../assets/Icon';
import { useState } from 'react';
import axios from 'axios';

const cx = classNames.bind(style);

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        const newUser = {
            username: username,
            password: password,
        };
        try {
            const res = await axios.post(
                'http://localhost:5000/api/auth/login', // đổi URL theo backend của bạn
                newUser,
                { withCredentials: true },
            );

            console.log('Đăng nhập thành công:', res.data);

            // lưu token
            if (res.data.accessToken) {
                localStorage.setItem('accessToken', res.data.accessToken);
            }

            // chuyển hướng
            window.location.href = '/';
        } catch (err) {
            const msg = err.response?.data?.message || 'Đăng nhập thất bại';
            console.error('Lỗi đăng nhập:', msg);
            alert(msg);
        }
    };

    return (
        <div className={cx('wrapper')}>
            <div className={cx('container')}>
                <div className={cx('header-container')}>
                    <span>
                        <SpotifyIcon width="2.8rem" height="2.8rem" />
                    </span>
                    <span className={cx('header-title')}>Log in to Spotify</span>
                </div>
                <form className={cx('input-login-container')} onSubmit={handleLogin}>
                    <span className={cx('input-login-title')}>Email or username</span>
                    <input
                        className={cx('input-login')}
                        placeholder="Email or username"
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        className={cx('input-login')}
                        placeholder="Password"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className={cx('continue-btn')}>Continue</button>
                </form>
                <div className={cx('sign-up')}>
                    <span style={{ color: 'var(--gray)' }}>Don't have an account?</span>
                    <a href="/" className={cx('sign-up-link')}>
                        Sign up for Spotify
                    </a>
                </div>
            </div>
        </div>
    );
}

export default Login;
