import classNames from 'classnames/bind';
import style from './MainView.module.scss';
import { useEffect, useState } from 'react';
import { getAllSongs } from '../../../redux/apiRequest';
import { useDispatch, useSelector } from 'react-redux';

const cx = classNames.bind(style);

function Songs() {
    const dispatch = useDispatch();
    const songs = useSelector((state) => state.songs.songs?.allSongs);
    const showArtist = [];

    useEffect(() => {
        getAllSongs(dispatch);
    }, [dispatch]);

    console.log(songs);

    return (
        <div className={cx('wrapper')}>
            <div className={cx('header-btn')}>
                <button>All</button>
                <button>Music</button>
                <button>Podcasts</button>
            </div>
            <div className={cx('all-song')}>
                {songs.map((song) => (
                    <div key={song._id} className={cx('song-item')}>
                        <img className={cx('song-avatar')} src={song.avatar} alt={song.name}></img>
                        <span>{song.artist.name}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Songs;
