import classNames from 'classnames/bind';
import style from './Login.module.scss';
import { SpotifyIcon } from '../../assets/Icon';
import { useState } from 'react';
import { loginUser } from '../../redux/apiRequest';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
// import { Password } from 'primereact/password';
import { Cascader, Input, Select, Space } from 'antd';
const cx = classNames.bind(style);
const { Option } = Select;
function Login() {
    const [userName, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        const newUser = {
            userName: userName,
            password: password,
        };
        loginUser(newUser, dispatch, navigate);
    };
    const selectBefore = (
        <Select defaultValue="http://">
            <Option value="http://">http://</Option>
            <Option value="https://">https://</Option>
        </Select>
    );
    const selectAfter = (
        <Select defaultValue=".com">
            <Option value=".com">.com</Option>
            <Option value=".jp">.jp</Option>
            <Option value=".cn">.cn</Option>
            <Option value=".org">.org</Option>
        </Select>
    );
    return (
        <div className={cx('wrapper')}>
            <div className={cx('container')}>
                <div className={cx('header-container')}>
                    <span>
                        <SpotifyIcon width="3.2rem" height="3.2rem" />
                    </span>
                    <span className={cx('header-title')}>Log in to Spotify</span>
                </div>
                <form className={cx('input-login-container')} onSubmit={handleLogin}>
                    <span className={cx('input-login-title')}>Email or username</span>
                    <input
                        className={cx('input-login')}
                        placeholder="Email or username"
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        className={cx('input-login')}
                        // type="password"
                        placeholder="Password"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <Input addonBefore={selectBefore} addonAfter={selectAfter} defaultValue="mysite" />

                    {/* <Password value={password} onChange={(e) => setPassword(e.target.value)} toggleMask /> */}

                    <button className={cx('continue-btn')}>Continue</button>
                </form>
                <div className={cx('sign-up')}>
                    <span style={{ color: 'var(--gray)' }}>Don't have an account?</span>
                    <a href="/" className={cx('sign-up-link')}>
                        Sign up for Spotify
                    </a>
                </div>
            </div>
        </div>
    );
}

export default Login;
