import classNames from 'classnames/bind';
import style from './MainView.module.scss';
import { useEffect, useState } from 'react';
import { getAllSongs } from '../../../redux/apiRequest';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';

const cx = classNames.bind(style);

function Songs() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [songs, setSong] = useState([]);

    const handleGetSongs = async (e) => {
        e.preventDefault();
        const songs = {
            name: songs.name,
            audioUrl: songs.audioUrl,
            avatar: songs.avatar,
        };
        getAllSongs(songs, dispatch, navigate);
    };

    useEffect(() => {
        getAllSongs(dispatch, navigate); // gọi khi component load
    }, [dispatch, navigate]);

    return (
        <div className={cx('wrapper')}>
            <div className={cx('header-btn')}>
                <button>All</button>
                <button>Music</button>
                <button>Podcasts</button>
            </div>
            <div className={cx('all-song')}>
                {songs.map((song) => (
                    <div key={song._id} className={cx('song-item')}>
                        <img src={song.avatar} alt={song.name}></img>
                        <span>{song.name}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Songs;
