import classNames from 'classnames/bind';
import style from './Login.module.scss';
import { SpotifyIcon } from '../../assets/Icon';
import { useState } from 'react';
import axios from 'axios';

const cx = classNames.bind(style);

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        const newUser = {
            username,
            password,
        };

        try {
            const res = await axios.post('http://localhost:5000/api/auth/login', newUser, {
                withCredentials: true, // nếu dùng cookie (refreshToken)
            });
            console.log('Đăng nhập thành công:', res.data);

            // Ví dụ: lưu token vào localStorage
            localStorage.setItem('accessToken', res.data.accessToken);

            // Chuyển hướng sang trang khác
            window.location.href = '/';
        } catch (err) {
            console.error('Lỗi đăng nhập:', err.response?.data || err.message);
            alert(err.response?.data || 'Đăng nhập thất bại');
        }
    };

    return (
        <div className={cx('wrapper')}>
            <div className={cx('container')}>
                <div className={cx('header-container')}>
                    <span>
                        <SpotifyIcon width="2.4rem" height="2.4rem" />
                    </span>
                    <span className={cx('header-title')}>Log in to Spotify</span>
                </div>
                <form className={cx('input-login-container')}>
                    <span className={cx('input-login-title')}>Email or username</span>
                    <input
                        className={cx('input-login')}
                        placeholder="Email or username"
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <button className={cx('continue-btn')} onClick={handleLogin}>
                        Continue
                    </button>
                </form>
                <div className={cx('sign-up')}>
                    <span style={{ color: 'var(--gray)' }}>Don't have an account?</span>
                    <a href="/" className={cx('sign-up-link')}>
                        Sign up for Spotify
                    </a>
                </div>
            </div>
        </div>
    );
}

export default Login;
