import React, { useEffect } from 'react';
import Header from '../../components/DefaultLayout/Header';
import Sidebar from '../../components/DefaultLayout/Sidebar';
import ControlBar from '../../components/ControlBar';
import MainView from '../../components/DefaultLayout/MainView';
import classNames from 'classnames/bind';
import style from './Home.module.scss';
import RightActionBar from '../../components/DefaultLayout/RightActionBar';
import ArtistDetail from '../../components/ArtistDetail';
import { useNavigate } from 'react-router-dom';
import { getAllUsers } from '../../redux/apiRequest';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { default as jwt_decode } from 'jwt-decode';
import { loginSuccess } from '../../redux/authSlice';

const cx = classNames.bind(style);

export default function Home() {
    let axiosJWT = axios.create();
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const user = useSelector((state) => state.auth.login?.currentUser);

    useEffect(() => {
        if (!user) {
            alert('Bạn cần đăng nhập trước');
            navigate('/login');
        }
        if (user?.accessToken) {
            getAllUsers(user?.accessToken, dispatch);
        }
    }, []);

    const refreshToken = async () => {
        try {
            const res = await axios.post('http://localhost:5000/api/auth/refresh', {
                withCredentials: true,
            });
            return res.data;
        } catch (error) {
            console.log(error);
        }
    };

    axiosJWT.interceptors.request.use(async (config) => {
        const date = new Date();
        const decodedToken = jwt_decode(user?.accessToken);
        if (decodedToken.exp < date.getTime() / 1000) {
            const data = await refreshToken();
            const refreshUser = {
                ...user,
                accessToken: data.accessToken,
            };
            dispatch(loginSuccess(refreshToken));
        }
    });

    return (
        <div className={cx('wrapper')}>
            <Header />
            <div className={cx('content')}>
                <Sidebar />
                {/* <Routes>
                        <Route path="/" element={<MainView />} />
                        <Route path="/artist/:id" element={<ArtistDetail />} />
            </Routes> */}
                <MainView />
                <RightActionBar />
            </div>
            <ControlBar />
        </div>
    );
}
